import express from 'express';
import cors from 'cors';
import Database from 'better-sqlite3';
import path from 'path';
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

const app = express();
const PORT = 3001;

// データベース接続（親ディレクトリのcontent.db）
const dbPath = path.resolve(__dirname, '../../content.db');
const db = new Database(dbPath);

app.use(cors());
app.use(express.json());

// ========== Articles API ==========

// 記事一覧取得
app.get('/api/articles', (req, res) => {
  const { status, branding_type, category } = req.query;

  let query = `
    SELECT a.*, GROUP_CONCAT(t.name) as tags
    FROM articles a
    LEFT JOIN article_tags at ON a.id = at.article_id
    LEFT JOIN tags t ON at.tag_id = t.id
  `;

  const conditions: string[] = [];
  const params: string[] = [];

  if (status) {
    conditions.push('a.status = ?');
    params.push(status as string);
  }
  if (branding_type) {
    conditions.push('a.branding_type = ?');
    params.push(branding_type as string);
  }
  if (category) {
    conditions.push('a.category = ?');
    params.push(category as string);
  }

  if (conditions.length > 0) {
    query += ' WHERE ' + conditions.join(' AND ');
  }

  query += ' GROUP BY a.id ORDER BY a.priority DESC, a.created_at DESC';

  const articles = db.prepare(query).all(...params);
  res.json(articles);
});

// 記事詳細取得
app.get('/api/articles/:id', (req, res) => {
  const article = db.prepare(`
    SELECT a.*, GROUP_CONCAT(t.name) as tags
    FROM articles a
    LEFT JOIN article_tags at ON a.id = at.article_id
    LEFT JOIN tags t ON at.tag_id = t.id
    WHERE a.id = ?
    GROUP BY a.id
  `).get(req.params.id);

  if (article) {
    res.json(article);
  } else {
    res.status(404).json({ error: 'Article not found' });
  }
});

// 記事作成
app.post('/api/articles', (req, res) => {
  const { title, branding_type, category, status, file_path, priority, tags } = req.body;

  const result = db.prepare(`
    INSERT INTO articles (title, branding_type, category, status, file_path, priority)
    VALUES (?, ?, ?, ?, ?, ?)
  `).run(title, branding_type, category, status || 'idea', file_path, priority || 0);

  const articleId = result.lastInsertRowid;

  // タグの関連付け
  if (tags && Array.isArray(tags)) {
    const insertTag = db.prepare('INSERT OR IGNORE INTO tags (name) VALUES (?)');
    const getTagId = db.prepare('SELECT id FROM tags WHERE name = ?');
    const linkTag = db.prepare('INSERT INTO article_tags (article_id, tag_id) VALUES (?, ?)');

    for (const tagName of tags) {
      insertTag.run(tagName);
      const tag = getTagId.get(tagName) as { id: number };
      if (tag) {
        linkTag.run(articleId, tag.id);
      }
    }
  }

  res.json({ id: articleId, message: 'Article created' });
});

// 記事更新
app.put('/api/articles/:id', (req, res) => {
  const { title, branding_type, category, status, file_path, note_url, priority } = req.body;

  db.prepare(`
    UPDATE articles
    SET title = ?, branding_type = ?, category = ?, status = ?,
        file_path = ?, note_url = ?, priority = ?, updated_at = CURRENT_TIMESTAMP
    WHERE id = ?
  `).run(title, branding_type, category, status, file_path, note_url, priority, req.params.id);

  res.json({ message: 'Article updated' });
});

// 記事ステータス更新
app.patch('/api/articles/:id/status', (req, res) => {
  const { status } = req.body;

  let updateQuery = `
    UPDATE articles
    SET status = ?, updated_at = CURRENT_TIMESTAMP
  `;

  if (status === 'published') {
    updateQuery = `
      UPDATE articles
      SET status = ?, updated_at = CURRENT_TIMESTAMP, published_at = CURRENT_TIMESTAMP
    `;
  }

  updateQuery += ' WHERE id = ?';

  db.prepare(updateQuery).run(status, req.params.id);
  res.json({ message: 'Status updated' });
});

// 記事削除
app.delete('/api/articles/:id', (req, res) => {
  db.prepare('DELETE FROM article_tags WHERE article_id = ?').run(req.params.id);
  db.prepare('DELETE FROM articles WHERE id = ?').run(req.params.id);
  res.json({ message: 'Article deleted' });
});

// ========== Branding API ==========

// ブランディング一覧取得
app.get('/api/branding', (_req, res) => {
  const branding = db.prepare('SELECT * FROM branding').all();
  res.json(branding);
});

// ========== Tags API ==========

// タグ一覧取得
app.get('/api/tags', (_req, res) => {
  const tags = db.prepare(`
    SELECT t.*, COUNT(at.article_id) as article_count
    FROM tags t
    LEFT JOIN article_tags at ON t.id = at.tag_id
    GROUP BY t.id
    ORDER BY article_count DESC
  `).all();
  res.json(tags);
});

// ========== Stats API ==========

// 統計情報取得
app.get('/api/stats', (_req, res) => {
  const totalArticles = db.prepare('SELECT COUNT(*) as count FROM articles').get() as { count: number };
  const byStatus = db.prepare(`
    SELECT status, COUNT(*) as count
    FROM articles
    GROUP BY status
  `).all();
  const byBranding = db.prepare(`
    SELECT branding_type, COUNT(*) as count
    FROM articles
    GROUP BY branding_type
  `).all();

  res.json({
    total: totalArticles.count,
    byStatus,
    byBranding
  });
});

// ========== Categories API ==========

// カテゴリ一覧取得
app.get('/api/categories', (_req, res) => {
  const categories = db.prepare(`
    SELECT DISTINCT category, COUNT(*) as count
    FROM articles
    WHERE category IS NOT NULL
    GROUP BY category
    ORDER BY count DESC
  `).all();
  res.json(categories);
});

app.listen(PORT, () => {
  console.log(`Server running on http://localhost:${PORT}`);
});

