* { box-sizing: border-box; margin: 0; padding: 0; }
body { font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif; background: #0f0f0f; color: #e0e0e0; min-height: 100vh; }
a { color: #60a5fa; text-decoration: none; }
a:hover { text-decoration: underline; }

.container { max-width: 1200px; margin: 0 auto; padding: 20px; }

/* Navigation */
nav { background: #1a1a1a; border-bottom: 1px solid #333; padding: 15px 0; position: sticky; top: 0; z-index: 100; }
.nav-inner { display: flex; align-items: center; gap: 30px; }
.logo { font-size: 1.2rem; font-weight: bold; color: #fff !important; }
.links { display: flex; gap: 20px; }
.links a { color: #999; }
.links a:hover, .links a.active { color: #fff; text-decoration: none; }

/* Typography */
h1 { font-size: 1.8rem; margin-bottom: 20px; }
h2 { font-size: 1.3rem; margin-bottom: 15px; color: #ccc; }

/* Cards */
.card { background: #1a1a1a; border: 1px solid #333; border-radius: 8px; padding: 20px; margin-bottom: 20px; }
.card-header { display: flex; justify-content: space-between; align-items: center; margin-bottom: 15px; }

/* Grid */
.grid { display: grid; gap: 20px; }
.grid-2 { grid-template-columns: repeat(2, 1fr); }
.grid-3 { grid-template-columns: repeat(3, 1fr); }
.grid-4 { grid-template-columns: repeat(4, 1fr); }
@media (max-width: 768px) { .grid-2, .grid-3, .grid-4 { grid-template-columns: 1fr; } }

/* Stats */
