.badge-review { background: #3f3f00; color: #fbbf24; }
.badge-published { background: #14532d; color: #4ade80; }
.badge-social_misfit_ai { background: #7c3aed20; color: #a78bfa; }
.badge-philosopher { background: #0891b220; color: #22d3ee; }
